    fields {
      slug
      publishedAt
    }
    frontmatter {
      title
      excerpt
      date(formatString: "MMMM Do YYYY")
      rawDate: date
      canonicalLink
      tags
      image {
        childImageSharp {
          resize(width: 1500, height: 1500) {
            src
