    timeToRead
    fields {
      slug
      publishedAt
    }
    frontmatter {
      title
      excerpt
      date(formatString: "MMMM Do YYYY")
      rawDate: date
      canonicalLink
      tags
      image {
        childImageSharp {
          resize(width: 1500, height: 1500) {
            src
          }
          fluid(maxWidth: 786) {
            ...GatsbyImageSharpFluid
          }
        }
      }
      imageAuthor
      imageAuthorLink
      imageTitle
      showImageInArticle
      author {
        id
        bio
        twitter
        avatar {
          childImageSharp {
            fixed(width: 64, height: 64, quality: 75, traceSVG: {turdSize: 10, background: "#f6f2f8", color: "#e0d6eb"}) {
              ...GatsbyImageSharpFixed_tracedSVG
            }
          }
        }
        fields {
          slug
        }
      }
    }
  }
}

