query:
query usersMisiekDevGatsbyWwwSrcTemplatesTagsJs2881487692(
  $tags: [String]
) {
  allMdx(limit: 2000, sort: {fields: [frontmatter___date, fields___slug], order: DESC}, filter: {frontmatter: {tags: {in: $tags}}, fileAbsolutePath: {regex: "/docs.blog/"}, fields: {released: {eq: true}}}) {
    totalCount
    edges {
      node {
        ...BlogPostPreview_item
      }
    }
  }
}

fragment BlogPostPreview_item on Mdx {
  excerpt
  fields {
    slug
  }
  frontmatter {
    excerpt
    title
    date
    author {
      id
      fields {
        slug
      }
      avatar {
        childImageSharp {
          fixed(width: 32, height: 32, quality: 80, traceSVG: {turdSize: 10, background: "#f6f2f8", color: "#e0d6eb"}) {
            ...GatsbyImageSharpFixed_tracedSVG
          }
        }
      }
    }
    cover {
      childImageSharp {
        fluid(maxWidth: 700, quality: 80) {
          ...GatsbyImageSharpFluid_withWebp
        }
      }
    }
  }
}

fragment GatsbyImageSharpFixed_tracedSVG on ImageSharpFixed {
  tracedSVG
  width
  height
  src
  srcSet
}

fragment GatsbyImageSharpFluid_withWebp on ImageSharpFluid {
  base64
  aspectRatio
  src
  srcSet
  srcWebp
  srcSetWebp
  sizes
}
