      title
      overview
      issue
      disableTableOfContents
      tableOfContentsDepth
    }
    ...MarkdownPageFooterMdx
  }
}

fragment MarkdownPageFooterMdx on Mdx {
  parent {
    __typename
    ... on File {
      relativePath
    }
  }
}
