    name
    description
    location
    website
    github
    image {
      childImageSharp {
        fluid {
          ...GatsbyImageSharpFluid
        }
      }
    }
    for_hire
    hiring
    portfolio
    type
    fields {
      slug
    }
  }
  allSitesYaml(filter: {fields: {hasScreenshot: {eq: true}}}) {
