  mdx(fields: {slug: {eq: $path}}) {
    body
    excerpt
    timeToRead
    tableOfContents
    fields {
      slug
      anchor
    }
    frontmatter {
      title
      overview
      issue
      disableTableOfContents
      tableOfContentsDepth
    }
    ...MarkdownPageFooterMdx
  }
}

fragment MarkdownPageFooterMdx on Mdx {
  parent {
    __typename
    ... on File {
      relativePath
    }
  }
}
