query:
query usersMisiekDevGatsbyWwwSrcPagesDocsNodeApisJs2140480171 {
  file(relativePath: {regex: "/src.*api-node-docs.js/"}) {
    childrenDocumentationJs {
      name
      ...DocumentationFragment
    }
  }
  nodeAPIs: allGatsbyApiCall(filter: {group: {eq: "NodeAPI"}}) {
    group(field: name) {
      ...ApiCallFragment
    }
  }
}

fragment DocumentationFragment on DocumentationJs {
  kind
  ...DocumentationDescriptionFragment
  ...DocumentationExampleFragment
  ...DocumentationParamsFragment
  ...DocumentationReturnsFragment
}

fragment ApiCallFragment on GatsbyAPICallGroupConnection {
  name: fieldValue
  nodes {
    file
    codeLocation {
      start {
        line
      }
      end {
        line
      }
    }
  }
}

fragment DocumentationDescriptionFragment on DocumentationJs {
  name
  description {
    childMdx {
      body
    }
  }
  deprecated {
    childMdx {
      body
    }
  }
}

fragment DocumentationExampleFragment on DocumentationJs {
  examples {
    highlighted
  }
  type {
    typeDef {
      examples {
        highlighted
      }
    }
  }
}

fragment DocumentationParamsFragment on DocumentationJs {
  ...DocumentationParamsFragmentR2
  params {
    ...DocumentationParamsFragmentR2
  }
  properties {
    ...DocumentationParamsFragmentR2
  }
  members {
    static {
      ...DocumentationParamsFragmentR2
    }
  }
  type {
    typeDef {
      ...DocumentationParamsFragmentR2
    }
  }
}

fragment DocumentationReturnsFragment on DocumentationJs {
  returns {
    ...DocumentationDescriptionFragment
    ...DocumentationTypeFragment
  }
}

fragment DocumentationTypeFragment on DocumentationJs {
  optional
  type {
    name
    type
    elements
    expression
    applications
  }
}

fragment DocumentationParamsFragmentR2 on DocumentationJs {
  ...DocumentationParamsFragmentR1
  params {
    ...DocumentationParamsFragmentR1
  }
  properties {
    ...DocumentationParamsFragmentR1
  }
  members {
    static {
      ...DocumentationParamsFragmentR1
    }
  }
  type {
    typeDef {
      ...DocumentationParamsFragmentR1
    }
  }
}

fragment DocumentationParamsFragmentR1 on DocumentationJs {
  ...DocumentationParamsFieldsFragment
  params {
    ...DocumentationParamsFieldsFragment
  }
  properties {
    ...DocumentationParamsFieldsFragment
  }
  members {
    static {
      ...DocumentationParamsFieldsFragment
    }
  }
  type {
    typeDef {
      ...DocumentationParamsFieldsFragment
    }
  }
}

fragment DocumentationParamsFieldsFragment on DocumentationJs {
  ...DocumentationParamBase
  members {
    static {
      ...DocumentationParamBase
      type {
        typeDef {
          ...DocumentationParamBase
        }
      }
    }
  }
  type {
    typeDef {
      ...DocumentationParamBase
      members {
        static {
          ...DocumentationParamBase
        }
      }
    }
  }
}

fragment DocumentationParamBase on DocumentationJs {
  params {
    name
    ...DocumentationDescriptionFragment
    ...DocumentationExampleFragment
    ...DocumentationTypeFragment
  }
  properties {
    name
    ...DocumentationExampleFragment
    ...DocumentationTypeFragment
  }
  ...DocumentationTypeFragment
  ...DocumentationDescriptionFragment
  ...DocumentationExampleFragment
  ...DocumentationReturnsFragment
}
