  $slug: String!
) {
  markdownRemark(fields: {slug: {eq: $slug}}) {
    ...MarkdownPageFooter
  }
  npmPackage(slug: {eq: $slug}) {
    name
    description
    keywords
    lastPublisher {
      name
      avatar
    }
    repository {
      url
    }
    readme {
